// Constructor
var Class = function (val1, val2) {
  //do something... or not.
  this.val1 = val1;
  this.val2 = val2;
}

// properties and methods
// two different ways to do the methods:
// 1. Single Prototype Object declaration
Class.prototype = {
  val1    : "default value",
  method  : function(argument) {
    this.val2 = argument + " some value";
  }
};

// 2. Individual Prototype.someMethod declarations
function doSomething() {
  window.alert(process.version);
}

Class.prototype.doSomething = doSomething;


// Two different ways to export:
/** 
 * 1. call like this:
 * var Something = require('something');
 * var aThing = new Something();
**/
module.exports = Class;

/** 
 * 2. call like this:
 * var Something = require('something');
 * var aThing = new Something.Class();
**/
//exports.Class = Class;

